function Res = Chi23_Turing_From_CW(Res,coeff,ind_k)
    
    Res.Stat              = Res.Stat.Met.Norm(Res.Stat);
        
    Res.CW.Met.MI_Matrix   = @Chi23_MI_Matrix;
    Res.CW.Stab            =  Chi23_MI(Res.CW);
    Res.Stat.Sol.Psi_o     =  1E-10*ones(1,Res.Stat.Space.N);
    Res.Stat.Sol.Psi_e     =  1E-10*ones(1,Res.Stat.Space.N);
    
    
%    [i_k,i_c] = find(real(Res.CW.Stab.Value) == max(max(real(Res.CW.Stab.Value))));
    if Res.CW.In.eps >= 0
        
        sg = -1;
        i_k = Res.Stat.Space.N-Res.Stat.In.mu_bl+1;% Res.Stat.In.mu_bl+1;
        
    else
        
        sg = 1;        
    
    end
    
    i_k = find(abs(Res.Stat.Space.k) == abs(Res.Stat.Space.k(i_k)));
    
    i_c = zeros(1,2);
    
    [~,i_c(1)]  = max(real(Res.CW.Stab.Value(i_k(1),:)) );
    [~,i_c(2)]  = max(real(Res.CW.Stab.Value(i_k(2),:)) );
    
    Res.Stat.Sol.Psi_o(1)  = Res.CW.Sol.Psi_o*Res.Stat.Space.N;       
    Res.Stat.Sol.Psi_e(1)  = Res.CW.Sol.Psi_e*Res.Stat.Space.N;      
    
    Res.Stat.Sol.Psi_o(2)  = (Res.CW.Stab.Vector(i_k(ind_k)).V(1,i_c(ind_k))  )*coeff*Res.Stat.Space.N;       %+ Res.CW.Stab.Vector(i_k(2)).V(1,i_c(2) )
    Res.Stat.Sol.Psi_e(2)  = (Res.CW.Stab.Vector(i_k(ind_k)).V(2,i_c(ind_k)))*coeff*Res.Stat.Space.N;     %+ Res.CW.Stab.Vector(i_k(2)).V(2,i_c(2) )
    
    Res.Stat.Sol.Psi_o(end)  = (Res.CW.Stab.Vector(i_k(ind_k)).V(3,i_c(ind_k)) )*coeff*Res.Stat.Space.N;       %+ Res.CW.Stab.Vector(i_k(2)).V(3,i_c(2))
    Res.Stat.Sol.Psi_e(end)  = (Res.CW.Stab.Vector(i_k(ind_k)).V(4,i_c(ind_k)))*coeff*Res.Stat.Space.N;    %+ Res.CW.Stab.Vector(i_k(2)).V(4,i_c(2))  
    
